using UnityEngine;
using UnityEngine.Rendering;
using UnityEngine.Rendering.Universal;

public class StylizedLightingSetup : MonoBehaviour
{
    public static void SetupStylizedLighting()
    {
        // Configure ambient lighting for low poly style
        RenderSettings.ambientMode = AmbientMode.Trilight;
        RenderSettings.ambientSkyColor = new Color(0.5f, 0.7f, 1.0f, 1.0f); // Light blue sky
        RenderSettings.ambientEquatorColor = new Color(0.4f, 0.4f, 0.6f, 1.0f); // Neutral horizon
        RenderSettings.ambientGroundColor = new Color(0.2f, 0.2f, 0.3f, 1.0f); // Dark ground
        RenderSettings.ambientIntensity = 0.6f;

        // Set fog for atmospheric depth
        RenderSettings.fog = true;
        RenderSettings.fogColor = new Color(0.7f, 0.8f, 1.0f, 1.0f);
        RenderSettings.fogMode = FogMode.Linear;
        RenderSettings.fogStartDistance = 30f;
        RenderSettings.fogEndDistance = 100f;

        // Configure shadow settings for crisp shadows
        QualitySettings.shadows = ShadowQuality.All;
        QualitySettings.shadowResolution = ShadowResolution.High;
        QualitySettings.shadowDistance = 50f;
        QualitySettings.shadowCascades = 2;

        Debug.Log("Stylized lighting setup complete!");
    }

    [ContextMenu("Setup Stylized Lighting")]
    public void Setup()
    {
        SetupStylizedLighting();
    }
}

